import 'package:flutter/material.dart';

class ImageLearn extends StatelessWidget{
  ImageLearn({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(),
      body: Column(children: [
        SizedBox(
          width: 400,
          height: 500,
          child: PngImage(name: ImageItems().appleBookWithoutPath),
        ),
        Image.network('data:image/png;base64,'),
        //errorBuilder: (context, error, stackTrace) => const Icon(Icons.abc_outlined),
      ]),
    );
  }
}

class ImageItems{
  final String appleWithBook = "assets/apple-and-book-png-transparent-apple-and-book-images-274565.png";
  final String appleBook = "assets/png/aaa";
  final String appleBookWithoutPath = "aaa";
}

class PngImage extends StatelessWidget{
  const PngImage({Key? key, required this.name}) : super(key: key);
  final String name;

  @override
  Widget build(BuildContext context) {
    return Image.asset('assets/png/$name.png', fit: BoxFit.cover);
  }

}